import React, { Component } from "react";
import ArtWalkPublishedDataService from "../services/artwalk-published.service";
//import { Link } from "react-router-dom";


//import Pagination from "@material-ui/lab/Pagination";

export default class ArtWalksPublished extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchTitle = this.onChangeSearchTitle.bind(this);
    this.retrieveArtwalks = this.retrieveArtwalks.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveArtWalk = this.setActiveArtWalk.bind(this);
    this.removeAllArtWalks = this.removeAllArtWalks.bind(this);
    this.searchTitle = this.searchTitle.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
    this.handlePageSizeChange = this.handlePageSizeChange.bind(this);

    this.state = {
      artwalks: [],
      currentArtWalk: null,
      currentIndex: -1,
      searchTitle: "",

      page: 1,
      count: 0,
      pageSize: 1,
    };

    this.pageSizes = [1, 6, 9];
  }

  componentDidMount() {
    this.retrieveArtwalks();
  }

  onChangeSearchTitle(e) {
    const searchTitle = e.target.value;

    this.setState({
      searchTitle: searchTitle
    });
  }


  getRequestParams(searchTitle, page, pageSize) {
    let params = {};

    if (searchTitle) {
      params["title"] = searchTitle;
    }

    if (page) {
      params["page"] = page - 1;
    }

    if (pageSize) {
      params["size"] = pageSize;
    }

    return params;
  }

 retrieveArtwalks() {
    const { searchTitle, page, pageSize } = this.state;
    const params = this.getRequestParams(searchTitle, page, pageSize);

    ArtWalkPublishedDataService.getAll(params)
      .then((response) => {
        const { artwalks, totalPages } = response.data;

        this.setState({
          artwalks: artwalks,
          count: totalPages,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveArtWalks();
    this.setState({
      currentArtWalk: null,
      currentIndex: -1
    });
  }

  setActiveArtWalk(artwalk, index) {
    this.setState({
      currentArtWalk: artwalk,
      currentIndex: index
    });
  }

  removeAllArtWalks() {
    ArtWalkPublishedDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch((e) => {
        console.log(e);
      });
  }

  handlePageChange(event, value) {
    this.setState(
      {
        page: value,
      },
      () => {
        this.retrieveArtwalks();
      }
    );
  }

  handlePageSizeChange(event) {
    this.setState(
      {
        pageSize: event.target.value,
        page: 1
      },
      () => {
        this.retrieveArtwalks();
      }
    );
  }

  searchTitle() {
    this.setState({
      currentArtWalk: null,
      currentIndex: -1
    });

    ArtWalkPublishedDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          artwalks: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchTitle, artwalks, currentArtWalk, currentIndex, } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by title"
              value={searchTitle}
              onChange={this.onChangeSearchTitle}/>
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.retrieveArtwalks}>
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>ArtWalk post</h4>

          <ul className="list-group">
            {artwalks && artwalks.map((artwalk, index) => (
              <li
                className={
                  "list-group-item " +
                  (index === currentIndex ? "active" : "")
                }
                ref={this.simulateClick}
                onClick={() => this.setActiveArtWalk(artwalk, index)}
                key={index}>
                <h4>{artwalk.title}</h4>
              </li>
            ))}
          </ul>

        </div>
        <div className="col-md-6">
          {currentArtWalk ? (
            <div>
              <h4>ArtWalk</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentArtWalk.title}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentArtWalk.description}
              </div>

            </div>
          ) : (
            <div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

